#[acmd_script(agent = "koopajr", scripts = ["game_landingairlw"], category = ACMD_GAME)]
fn bjr_landing_dair(fighter: &mut smash::lua2cpp::L2CAgentBase) {
    let lua_state = fighter.lua_state_agent;
    acmd!(lua_state, {
        frame(Frame=1)
        if(is_excute){
            WorkModule::on_flag(Flag=FIGHTER_STATUS_WORK_ID_FLAG_RESERVE_LANDING_ATTACK_AIR_SQUAT)
            JostleModule::set_status(true)
        }
    });
}

